mapper.update:
UPDATE user_info
SET user_level = #{userLevel,jdbcType=INTEGER}
WHERE user_id = #{userId,jdbcType=BIGINT}

mapper.select:
select  count(distinct user_id)  as userCount
from user_info
where 1=1

   and  owner_id = #{ownerId,jdbcType=BIGINT}  


  and  proxy_id = #{proxyId,jdbcType=BIGINT}

select  count(distinct proxy_id)  as userCount
from user_info
where owner_id = #{ownerId,jdbcType=BIGINT}
