mapper.select:
select order_id from user_order_detail
where user_id = #{userId}
and order_id = #{orderId}
and is_paid = #{isPaid}

mapper.update:
update user_order_detail
  SET is_paid = #{param.isPaid,jdbcType=BIGINT}
  WHERE user_id=#{param.userId,jdbcType=BIGINT}
  AND order_id = #{param.orderId,jdbcType=INTEGER}
