mapper.select:
SELECT COUNT(id) from owner_company_account_summmary
WHERE owner_id=#{ownerId}
and summary_type=#{summaryType}
and pdate=#{pdate}

mapper.update:
update owner_company_account_summmary
SET summary_money_count = summary_money_count +#{summaryMoneyCount},
summary_user_num = summary_user_num + #{summaryUserNum},
WHERE owner_id = #{ownerId}
AND summary_type = #{summaryType}
AND pdate = #{pdate}
